import React, {useEffect, useState} from "react";
import {useParams} from "react-router-dom";
import Country from "./Country";
import {apiGet} from "../utils/api";

const InvoiceDetail = () => {
    const {id} = useParams();
    const [invoice, setInvoice] = useState({});

    useEffect(() => {
        apiGet("/api/invoices/" + id).then((data) => setInvoice(data));
    }, []);

    useEffect(() => {
        apiGet("/api/persons/" + id)
          .then((data) => {
            setMovie({
              name: data.name,
              identificationNumber: data.year,
              director: data.director,
              actors: data.actors,
              genres: data.genres,
              isAvailable: data.isAvailable,
              dateAdded: data.dateAdded,
            });
          })
          .catch((error) => {
            console.error(error);
          });
      }, [id]);
    
      const genres = movie.genres?.map((item) => Genre[item]);
      const actors = movie.actors?.map((item) => item.name);
      const dateAdded = new Date(movie.dateAdded).toLocaleString();
    



    return (
        <>
            <div>
                <h1>Detail faktury</h1>
                <hr/>
                <h3>{invoice.id} ({invoice.invoiceNumber})</h3>
                <p>
                    <strong>Dodavatel</strong>
                    <br/>
                    
                </p>
                <p>
                    <strong>Odběratel</strong>
                    <br/>
                    
                </p>
                <p>
                    <strong>Datum vystavení</strong>
                    <br/>
                    {invoice.issued}
                </p>
                <p>
                    <strong>Datum splatnosti</strong>
                    <br/>
                    {invoice.dueDate}
                </p>
                <p>
                    <strong>Produkt</strong>
                    <br/>
                    {invoice.product}
                </p>
                <p>
                    <strong>Cena</strong>
                    <br/>
                    {invoice.price}
                </p>
                <p>
                    <strong>DPH</strong>
                    <br/>
                    {invoice.vat}
                </p>
                <p>
                    <strong>Poznámka</strong>
                    <br/>
                    {invoice.note}
                </p>
            </div>
        </>
    );
};

export default InvoiceDetail;
