import React, {useState, useEffect} from "react";
import { apiGet } from "../utils/api";
import { useParams } from "react-router-dom";


export const PurchasesAndSales = ({label, items, index}) => {

    const {identificationNumber} = useParams();

    const[purchases, setPurchases] = useState([]);
    const [sales, setSales] = useState([]);

    useEffect(() => {
        apiGet(`/api/identification/${identificationNumber}/purchases`).then((data) => setPurchases(data));
        apiGet(`/api/identification/${identificationNumber}/sales`).then((data) => setSales(data));
    })

    return (
        <div>
            <p>
                Vydané faktury
            </p>
            
            <table className="table table-bordered">
                <thead>
                    <tr>
                        <th>#</th>
                        <th></th>
                        
                    </tr>
                </thead>
                <tbody>
                {sales.map((item, index) => (
                        <tr key={index + 1}>
                            <td>{index + 1}</td>
                            <td>{item}</td>
                        </tr>
                    ))}
                  
                    
                </tbody>
            </table>

            <p>
                Přijaté faktury
            </p>
            
            <table className="table table-bordered">
                <thead>
                    <tr>
                        <th>#</th>
                        <th></th>
                        <th></th>
                        <th></th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                    {purchases.map((item, index) => (
                        <tr key={index + 1}>
                            <td>{index + 1}</td>
                            <td>{item.name}</td>
                            
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    
    )
}
